// import Axios from 'axios';

import { createStore } from 'vuex'

export default createStore({
  state: {
  },
  getters: {
  },
  mutations: {
  },
  actions: {
    REGISTER: ({ commit }, {username, email, password}) => {
        return new Promise((resolve, reject) => {
          Axios.post(`signup`, {
            username, email, password
          })
          .then(({data, status}) => {
            if (status === 201) {
              resolve(true);
            }
          })
          .catch(error => {
            reject(error);
          })
        });
    }
  },
  modules: {
  }
})
