import { createStore } from 'vuex'

export default createStore({
  state: {
  },
  getters: {
  },
  mutations: {
  },
  actions: {
    REGISTER: ({ commit }, {username, email, password}) => {

        return new Promise((resolve, reject) => {

          fetch(`test`, {
            method: "POST",
            body: JSON.stringify({username, email, password})
          })
          .then(response => response.json())
          .then(({data, status}) => {
              if (status === 201) {
                resolve(true);
              }
